const Signup = () => {
            return (
                <form className='account-form' onSubmit={(evt) => evt.preventDefault()}>
                    <div className={'account-form-fields ' + (option === 1 ? 'sign-in' : (option === 2 ? 'sign-up' : 'forgot')) }>
                        <input id='email' name='email' type='email' placeholder='E-mail' required />
